  `userId` int(11) NOT NULL,
  `mediaId` int(11) DEFAULT NULL,
  `comment` varchar(255) NOT NULL,
  `uploadTime` datetime NOT NULL,
  PRIMARY KEY (`Id`),
  KEY `userId` (`userId`),
